require(['jquery'], function($){
    /* Get src attribute from img */
    $(".bg-img").each(function() {
        var urlImg = $(this).attr('src'),
        parent = $(this).closest( '.img-bar' )
        $(this).remove();
        parent.css(
        {
            'background':'url("'+urlImg+'") no-repeat ',
            'background-size': 'cover',
            'background-position': 'center'
        });
    });
});
